/----- original saga generator function ----/

export function* putActiveChannel({ id }) {
  const currentUser = yield select(currentUserSelector);
  const userID = currentUser.get(`id`);
  yield console.log("sss", userID);
  yield call(() => axios.get(`/user/activeChannel/${userID}/${id}`));
}

/ --- test putActiveChannel using  runSaga() --- /

    1. /- define state for saga
    const state = { currentUser: { id: "userID" } };
    const id = "test";


    2. /- run saga will run entire generator function from first to last yield

    runSaga(
      {
        //dispatch: (action) => dispatched.push(action), //this generator doesnt dispatch action
        getState: () => state,
      },
      putActiveChannel,
      { id }
    );

    3. /- expect to call our mock axios.get
    expect(axios.get).toHaveBeenCalledTimes(1);
    axios.get().then((result) => {
      expect(result).toEqual("hi");
    });
    const userID = state.currentUser.id;
    expect(axios.get).toHaveBeenCalledWith(
      `/user/activeChannel/${userID}/${id}`
    );

