    (mapStateToProps, mapDispatchToProps) => (reactComponent) => {
      return {
        mapStateToProps,
        mapDispatchToProps,
        component: reactComponent,
      };
    }
  ),
  provider: ({ children }) => children,
};
const connect = reactRedux.connect;
const provider = reactRedux.provider;

