----------------------- app.js (math helpers implementation) ----------------
export const doAdd      = (a, b) => math.add(a, b);
export const doSubtract = (a, b) => math.subtract(a, b);
-----------------------------------------------------------------------------

--------------- now testing those implemenations --------------------------
import * as app from "./app";
import * as math from "./math";

/*
  *@test base function
  *@by running it
*/
test("calls math.add", () => {
    const add = math.add(1,2)
    expect(add).toBe(3)
})
/*
  *@test higher-order function
  *@by running it
*/
test("calls app.doAdd", () => {
    const appAdd = app.doAdd(1,2)
    expect(appAdd).toBe(3)
})
----------------------------------------------------------------------------

----------------- original apiService.get function -------

apiService.get = (a, b) => a + b;

--- Assign apiService.get to mock function (it will loose it original functionality)

apiService.get = jest.fn();

--jest.fn() is a normal function that doesnt return anything by default (as any js function)

console.log( apiService.get() )
//return undefined since fuction doesnt return anything


- then JEST comes with some testing methods to verirfy function is working -

//call funcion;
apiService.get();

//and expect
expect(apiService.get).toHaveBeenCalled();


- first call function and then test if function has been called twice with a param 'value'-

apiService.get('value');

expect(apiService.get).toHaveBeenCalledTimes(2);
expect(apiService.get).toHaveBeenCalledWith('value');


------------------------- ---------------------- -------------------------

------------- call console once executing a mock function-----

apiService.get = jest.fn(console.log('working'));


//call method
apiService.get()

---it will print to console but this is pointless cause it wont be testable cause jest.fn() do not return any value------

-------- jest.fn() function body as  (callback with return value)-----

 apiService.get = jest.fn(() => 2);

 //now call you mock
 console.log( apiService.get()) //return 2

- U can also pass arguments to your callback function -------

apiService.get = jest.fn((x) => x + 2);

console.log( apiService.get(2)) //return 4

 - Basically , U can do any trasformations inside your callback an then return any value you want and then verify in your acceptance criteria --

apiService.get = jest.fn((x) => x + 2 * 5 * 6);

--------- assertion to above -----------

expect(apiService.get(2)).toBe(62);

---------- ---------- ------------

------below is the same statement with 2 ways of declaring ------

apiService.get = jest.fn(()=>2) ===  apiService.getmockReturnValue(2)


------- you can specify what function return every time you call it ------

apiService.get
.mockReturnValueOnce(10)
.mockReturnValueOnce(10)
.mockReturnValue(20);

console.log(apiService.get());
console.log(apiService.get());
console.log(apiService.get());

 ----------------- original apiService.get function -------

apiService.get = (a, b) => a + b;

--- Assign apiService.get to mock function that returns Promise (it will loose it original functionality);

apiService.get = jest.fn(x => Promise.resolve(x));


-- make your assortion in Promise callback ----

  apiService.get(2).then((data) => {
    expect(data).toBe(2);
  });

