//---- 1a. Interface  combined naturally by intface duplication (not reccomended though)

interface Dude {
  name: string;
  age: number;
}

interface Dude {
  job: string;
  position: string;
}

let myEmployee10: Dude;
myEmployee10 = {
  name: 'Darek',
  age: 31,
  job: 'contract',
  position: 'developer',
};

//---- 1b. Interface can be extended as well (recommended) with the same effect

interface Dude1 {
  name: string;
  age: number;
}

interface Dude2 extends Dude1 {
  job: string;
  position: string;
}

let myEmployee11 = {} as Dude2;
myEmployee11 = {
  name: 'Darek',
  age: 31,
  job: 'contract',
  position: 'developer',
};

//---- 2. Interface one of another with or | operator


interface Dude100 {
  name: string;
  age: number;
}

interface Dude200 {
  job: string;
  position: string;
}

let myEmployee14: Dude100 | Dude200;

myEmployee14 = {
  name: 'string',
  job: 'contract',
  position: 'developer',
}; 
// a bit buggy cause it accepts 3 arguments it should accpet 2 or 4

