//default Promise return: unknown (even though we contextually provide string)

new Promise((resolve) => {resolve('1')});


//promise explicitly returns: string 

new Promise<string>((resolve) => {resolve('1')});

 //promise explicitly returns: object of any properties 

new Promise<Record<string,any>>((resolve) => {resolve({prop:1,prop2:'string'})});


//first promise returns string;

const firstPromise = new Promise<string>((resolve) => {resolve('1')});   

//second promise is to be chained to the first 

const secondPromise = firstPromise.then((firstRusult)=[firstResult])

//third Promise will be of type of last one 

const thirdPromise:string[] = firstPromise.then((secondPromise)=secondPromise)

//converting default Promise<Response> => Promise<unknown> => Promise<string>

const one:Promise<Response> = fetch('url') 
const three:Promise<unknown>  = one.then((result)=> result as unknown)
const four:Promise<string>  = three.then((result)=> result as string)

console.log(four.then((result)=>result ))

//the sequence has to be as the one above for TS to be happy about conversion

//function has been casted from Promise<unknown> to Promise<string> 

async function asyncCode() {
  return new Promise(resolve=>resolve('ready'))
}

const ss:Promise<string> = asyncCode() as Promise<string>
console.log(ss.then(()=>'greg'))

