//-------- multiple types generics

function createArray2MultipleTypes<T, U>(n: T, u: U): [T, U] {
  return [n, u];
}

createArray2MultipleTypes<number, number>(6, 4);
createArray2MultipleTypes<string, number[]>('2', [4]);
createArray2MultipleTypes<number, number[]>(2, [4]);

-----

function createArray<T>(...arg: T[]) {
  return [...arg];
}

createArray('one', 'two', 'three'); //string[]
createArray(3, 1, 4); //number[]
createArray([3], [2], [4]); //number[][]

---NOTICE----
//bellow should create an error
createArray({ ss: 3 }, { ee: 3 }, [6]); 

---THEREFORE----
//sometimes you need to help typescript to lock values types you want to use as below

//with type locking
createArray<{ [key: string]: number }>({ ss: 3 }, { ee: 3 }); //array{[key: string]: number }

--------------

type stringOrNumber = string | number;
function createArray1<T extends stringOrNumber>(...arg: T[]) {
  return [...arg];
}

createArray1(2, 4, 5);
createArray1('2', '4', '5');
createArray1([2], [4], [5]); //not accepted

