const Parent:React.FC<FCInterfaceProps> = ({name,age}) => {

   const [currentId, setCurrentId ] = useState(1); 
   const [loading, isLoaing ] = useState(false);
   const [error, setError ] = useState(null);


  useEffect(()=>{
    const mockApi = async () => {
      try{
          //below doesnt need to be in depedency array cause it doesnt affect outcome of hook
          isLoading(true)

           //below HAS TO be in depedency array cause it affects outcome of hook
          const response = await get({id:currentId})

          //below doesnt need to be in depedency array cause it doesnt affect outcome of hook
          isLoading(false)

          //response set to data useState hook
          setData((prevData=>Array.isArray(prevData)?[response,...prevData]:[response]))
        }
      catch(e) {
        //both dont effect 
        isLoading(false)
        hasError(e)
      }
    }
    //1. conditional call
    //this will require put 'error' constant in dependency array cause the outcome depend on it 
    // [currentId,error]
    if(!error) mockApi()

    //2 regular call
    mockApi()
   //every time currentId changes it will cause useEffect to re-rerender
  },[currentId])

